constant.ERRORMETHOD = () => {
  return new Promise((resolve, reject) => {
    return resolve(constant.ERROR + "|-9");
  });
};

constant.STARTPAGE = index => {
  let start = parseInt(index) * 10 - 10;
  if (start < 0) {
    start = 0;
  }
  return start;
};

constant.TODAY = () => {
  let today = new Date();
  let dd = today.getDate();
  let mm = today.getMonth() + 1;
  let yyyy = today.getFullYear();
  let hours = today.getHours();
  let minutes = today.getMinutes();
  if (dd < 10) {
    dd = "0" + dd;
  }

  if (mm < 10) {
    mm = "0" + mm;
  }

  if (hours < 10) {
    hours = "0" + hours;
  }

  if (minutes < 10) {
    minutes = "0" + minutes;
  }

  return today = dd + "/" + mm + "/" + yyyy + " " + hours + ":" + minutes;
};

constant.HOURS = () => {
  let today = new Date();
  let hours = today.getHours();
  let minutes = today.getMinutes();
  let seconds = today.getSeconds();

  if (hours < 10) {
    hours = "0" + hours;
  }

  if (minutes < 10) {
    minutes = "0" + minutes;
  }

  if (seconds < 10) {
    seconds = "0" + seconds;
  }

  return hours + " " + minutes + " : " + seconds;

};

constant.ENDPAGE = index => {
  let end = parseInt(index) * 10;
  if (end <= 0) {
    end = 10;
  }
  return end;
};

constant.getSum = (total, num) => {
  return total + num;
};

module.exports = constant;